import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {

  let today = new Date();

  let easternHours = today.getUTCHours()-4;
  if(easternHours < 0){
    easternHours += 24;
  }
  let easternMinutes = today.getUTCMinutes();
  if(easternMinutes < 10){
    easternMinutes = `0${easternMinutes}`;
  }
  let currentTimeEastern;
  if (easternHours > 12) {
    currentTimeEastern = `Current Eastern time ${easternHours-12}:${easternMinutes} PM`;
  } else {
    currentTimeEastern = `Current Eastern time ${easternHours}:${easternMinutes} AM`;
  }
  console.log(currentTimeEastern);

  let centralHours = today.getUTCHours()-5;
  if(centralHours < 0){
    centralHours += 24;
  }
  let centralMinutes = today.getUTCMinutes();
  if(centralMinutes < 10){
    centralMinutes = `0${centralMinutes}`;
  }
  let currentTimeCentral;
  if (centralHours > 12) {
    currentTimeCentral = `Current Central time ${centralHours-12}:${centralMinutes} PM`;
  } else {
    currentTimeCentral = `Current Central time ${centralHours}:${centralMinutes} AM`;
  }
  console.log(currentTimeCentral);

  let pacificHours = today.getUTCHours()-7;
  if(pacificHours < 0){
    pacificHours += 24;
  }
  let pacificMinutes = today.getUTCMinutes();
  if(pacificMinutes < 10){
    pacificMinutes = `0${pacificMinutes}`;
  }
  let currentTimePacific;
  if (pacificHours > 12) {
    currentTimePacific = `Current Pacific time ${pacificHours-12}:${pacificMinutes} PM`;
  } else {
    currentTimePacific = `Current Pacific time ${pacificHours}:${pacificMinutes} AM`;
  }
  console.log(currentTimePacific);

  let year = new Date().getYear();

  //second sunday in march
  let daylightOnMonth = 2;
  let firstSunday;
  for(let i = 1; i < 8; i++){
    var day = new Date(year, daylightOnMonth, i).getDay();
    if(day === 0) {
      firstSunday = i;
    }
  }
  let secondSunday = firstSunday + 7;
  console.log(firstSunday);
  console.log(secondSunday);

  return (
    <div className="App">
      <header className="App-header">
        <p>
          {currentTimeEastern}
        </p>
        <p>
          {currentTimeCentral}
        </p>
        <p>
          {currentTimePacific}
        </p>
      </header>
    </div>
  );
}

export default App;
